import { useState, useEffect } from "react";
import abi from './abi.js';
const ethers = require("ethers");

//contract address : 0x5FbDB2315678afecb367f032d93F642f64180aa3


//add getter functions inside the contract to actually access anything through mappings
//to access values of a struct in mapping, first return the promise, then use the variable rather than doing simultaneously
//use ethers@6.7.1 to deploy and use ethers@5.7.2 to run

function App() {

  const [address, setAddress] = useState("");
  const [arbiterAddress, setArbiterAddress] = useState("");
  const [arbiterStatus, setArbiterStatus] = useState("");
  const [candidatesCount, setCandidatesCount] = useState(0);
  const [provider, setProvider] = useState(null);
  const [votingContract, setContract] = useState("");
  const [arbiterApprovalAddress, setArbiterApprovalAddress] = useState("");

  async function connect() {
    if (window.ethereum) {
      console.log(ethers.version);
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      setProvider(provider);

      const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
      console.log('Connected accounts:', accounts);
      const signer = provider.getSigner(0);

      const votingContract = new ethers.Contract("0x5FbDB2315678afecb367f032d93F642f64180aa3", abi, signer);
      setContract(votingContract);

    }
  }

  useEffect(() => {
    connect();
  }, []);

  async function arbitersNum() {
    const totalArbiters = await votingContract.arbiterCount();
    const num = totalArbiters.toNumber();
    return num;
  }

  async function candidatesNum() {
    let totalCan = await votingContract.candidatesCount();
    let num = totalCan.toNumber();
    return num;
  }

  async function getArbiterCount() {
    const result = await arbitersNum()
    setAddress(result);
    setTimeout(() => {
      setAddress("");
    }, 1500);

  }

  async function getCandidatesCount() {
    const result = await candidatesNum()
    setCandidatesCount(result);
  }

  async function getArbiterStatus() {
    const resultObj = await votingContract.arbiterStatus(arbiterAddress);
    const result = resultObj.approved;
    setArbiterStatus(result.toString());
    setTimeout(() => {
      setArbiterAddress("");
      setArbiterStatus("");
    }, 1500);
  }

  async function approveArbiter() {
    await votingContract.approveArbiter(arbiterApprovalAddress);
  }

  return (
    <div className="App">
      <button id="getAddress" onClick={getArbiterCount}>Arbiter Count</button>
      <p>{address}</p>
      <br />
      <label for='arbiter?'>Arbiter </label>
      <input id='arbiter?' value={arbiterAddress}
        onChange={(event) => {
          setArbiterAddress(event.target.value)
        }} />
      <button id='isAnArbiter' onClick={getArbiterStatus}>Check</button>
      <p>Status : {arbiterStatus}</p>
      <br />
      <p>No. of candidates : {candidatesCount}</p>
      <button id='candidatesCount' onClick={getCandidatesCount}>Check</button>

      <br />
      <label htmlFor="approveArbiter">Enter arbiter for approval :</label>
      <input id='approveArbiter' value={arbiterApprovalAddress}
        onChange={(event) => {
          setArbiterApprovalAddress(event.target.value)
        }} />
      <button id="approve" onClick={approveArbiter}>Approve</button>

    </div>
  );
}

export default App;
